import { Fragment, useEffect } from "react";

const ProductList = (props) => {
    
    const product = { id: 5, name: 'M1 MacBook', description: 'Best MacBook on the Market', price: 3699.00 }
    
    useEffect( () => {
        console.log(product);
    })

    const headingStyle = {
        textalign: 'center',
    }

    const costStyle = {
        color: 'red',
    }

    const descriptionStyle = {
        fontsize: '10px'
    }

    return (
        <Product1
            name="{product.name}"
            description="{product.description}"
            cost="{product.cost}"></Product1>

    )

   
};

export default ProductList;



const ProductID = (props) => {

    return (
        <div>
            <h1 style={headingStyle}>{product.name} - ID:{product.id}</h1>
            <h4 style={costStyle}>${product.price}</h4>
            <p style={descriptionStyle}>{product.description}</p>
        </div>
    );
    ;
}
