const Product3 = (props) => {

    const [product, setProduct] = useState(props.product)

    const rowStyle = {
        borderTop: '1px solid #dddddd',
        padding: '20px'
    }

    const costStyle = {
        color: 'red',
    }

    return (
        <div style={rowStyle}>
            <h3>{product.name} - ID:{product.id}</h3>
            <h6 style={costStyle}>${product.price}</h6>
            <p>{product.description}</p>
        </div>
    )
    
};
