import { Fragment, useEffect, useState } from "react";

const ProductList = (props) => {
    
    const product = { id: 5, name: 'M1 MacBook', description: 'Best MacBook on the Market', price: 3699.00 }
    
    useEffect( () => {
        console.log(product);
    })

    return (
        <Fragment>
            <Product1
            name="{product.name}"
            description="{product.description}"
            cost="{product.cost}"></Product1>

            <Product3 
                product={product}></Product3>
        </Fragment>
    )
   
};

export default ProductList;

const Product1 = (props) => {

    const costStyle = {
        color: 'red',
    }

    return (
        <div>
            <h1>{product.name} - ID:{product.id}</h1>
            <h4 style={costStyle}>${product.price}</h4>
            <p>{product.description}</p>
        </div>
    );
    
};

const Product3 = (props) => {

    const [product, setProduct] = useState(props.product)

    const costStyle = {
        color: 'red',
    }

    return (
        <div>
            <h1>{product.name} - ID:{product.id}</h1>
            <h4 style={costStyle}>${product.price}</h4>
            <p>{product.description}</p>
        </div>
    )
    
};
